function TAB = RotationMatrix( Axis , Alpha )%   Rotation matrix from a frame B to a frame A%   Axis input argument indicates the rotation axis%   Alpha input argument indicates the rotation angle  (in radians)%   $\left\{\textbf{r}_{AB}\right\}=\textbf{T}_{AB}\,\left\{\textbf{r}_{AB}\right\}$s = sin( Alpha );c = cos( Alpha );switch( Axis )    case(1)        % 1-Rotation from A to B                TBA = [ 1  0  0 ;                   0  c  s ;                0  -s  c ];                TAB = transpose(TBA);            case(2)        % 2-Rotation from A to B                TBA = [  c  0  -s ;            0  1  0 ;            s  0  c ];                TAB = transpose(TBA);            case(3)        % 3-Rotation from A to B                TBA = [ c  s  0 ;             -s  c  0 ;            0  0  1 ];                TAB = transpose(TBA);        endend